import React, { useEffect, useState } from 'react';

const API_key = "dad69a5e01ddbe2e693acdd610cf1fd8";

const Holidays = () => {
    const [data, setData] = useState(null);
    const [country, setCountry] = useState("Dubrownik");

    const handleCountryChange = (e) => {
        setCountry(e.target.value);
    }

    const getData = (city_name) => {
        fetch(`https://api.openweathermap.org/data/2.5/weather?units=imperial&q=${city_name}&appid=${API_key}`, {
            method: 'GET'
        })
            .then(response => {
                return response.json();
            })
            .then(result => {
                setData(result);
                console.log(result)
            })
            .catch(err => {
                console.log('Błąd', err);
            })
    }

    useEffect(() => {
        getData(country);
    }, [country])

    if (!data) return <p>Trwa ładowanie danych</p>

    return <div>
        <select value={country} onChange={handleCountryChange}>
            <option value="Dubrownik">Dubrownik</option>
            <option value="Lublin">Lublin</option>
        </select>
        <h3 className='holiday-name'>{data.main.temp}</h3>
        <h3 className='holiday-name'>{data.main.feels_like}</h3>
        <h3 className='holiday-name'>{data.main.humidity}</h3>
        <h3 className='holiday-name'>{data.weather[0].description}</h3>
    </div>
}

export { Holidays }
