import React, {useState} from 'react';
import "./_opening.scss";


const Opening = () => {
    const [width, setWidth] = useState(300);
    const [height, setHeight] = useState(300);
    const [color, setColor] = useState('orangered');

    const handleHover = (newWidth, newHeight, newColor) => {
        setWidth(newWidth);
        setHeight(newHeight);
        setColor(newColor);
    }

    function hoverKTM(e) {
        console.log("Pan ktm")

    }
    function hoverBMW(e) {
        console.log("Pan bmw")
    }

    return (
        <>
            <section className="opening">
                <div className="opening_container container">
                    <div className="opening_left">
                        <h1 className="opening_header">Krotko o mnie:</h1>
                        <h3 className="opening_text">
                            Swoja przygode motocyklowa zaczalem w 2018r.<br/>
                            Przedewszystkim interesuje się turystyką motocyklowa.
                            Jednak posiadajac supermoto z kompletem kół terenowych od czasu do czasu wpadam na jakiś offroad i intensywaniejsza jazde wokół komina. <br/>
                        </h3>
                        <h1 className="opening_header">
                            Aktualnie jestem posiadaczem dwóch motocykli:
                        </h1>
                        <h2 onMouseEnter={() => handleHover(400, 400, 'orange')}
                            className="opening_text_moto" id="ktm_text">- KTM'a 1190 Adventure R (2015)</h2>
                        <h2 onMouseEnter={hoverBMW} className="opening_text_moto" id="bmw_text">- BMW G 650 Xmoto/~Xchallenge (2007)</h2>
                        <button className="button">WIECEJ O MNIE I MOICH MOTOCYKLACH</button>
                    </div>
                    <div className="opening_right">
                        <div style={{
                            width,
                            height,
                            borderColor:color
                        }} className="opening_photo ktm"></div>
                        <div className="opening_photo bmw"></div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Opening;

