import React, { useEffect, useState } from 'react';

const key = 'e92601251-c0a2-4s63-v73f-54041195480f';

const TEMPERATURY = () => {
    const [data, setData] = useState(null);
    const [country, setCountry] = useState("PL");

    const handleCountryChange = (e) => {
        setCountry(e.target.value);
    }

    const getData = (countryCode) => {
        fetch(`https://fer-api.coderslab.pl/v1/holidays?key=${key}&country=${countryCode}`, {
            method: 'GET'
        })
            .then(response => {
                return response.json();
            })
            .then(result => {
                setData(result.holidays);
            })
            .catch(err => {
                console.log('Błąd', err);
            })
    }

    useEffect(() => {
        getData(country);
    }, [country])

    if (!data) return <p>Trwa ładowanie danych</p>

    return <div>
        <select value={country} onChange={handleCountryChange}>
            <option value="PL">PL</option>
            <option value="GB">GB</option>
            <option value="US">US</option>
        </select>
        <ul className='list'>
            {data.map(holiday => {
                return <li key={holiday.uuid}>
                    <h3 className='holiday-name'>{holiday.name}</h3>
                    <div className='holiday-date'>{holiday.date}</div>
                </li>
            })}
        </ul>
    </div>
}

export { TEMPERATURY }
