import React, {useState} from 'react';
import "./_carousel.scss";

// const slider = document.querySelector('.slider');
// const leftArrow = document.querySelector('.left');
// const rightArrow = document.querySelector('.right');
//
// let sectionIndex = 0;
//
// rightArrow.addEventListener('click', function() {
//     sectionIndex = (sectionIndex < 2) ? sectionIndex + 1 : 2;
//     slider.style.transform = 'translate(' + (sectionIndex) * - 33 + '%)';
// });
//
// leftArrow.addEventListener('click', function() {
//     sectionIndex = (sectionIndex > 0) ? sectionIndex - 1 : 0;
//     slider.style.transform = 'translate(' + (sectionIndex) * - 33 + '%)';
//
// });

const Carousel = () => {


    const [currentIndex, setCurrentIndex] = useState(0)

    const next = () => {
        if (currentIndex < (2)) {
            setCurrentIndex(prevState => prevState + 1)
        }
    }

    const prev = () => {
        if (currentIndex > 0) {
            setCurrentIndex(prevState => prevState - 1)
        }
    }

    return (
        <div className="carousel-container container">
            <div className="carousel-wrapper">
                <button onClick={prev} className="left-arrow">
                    &lt;
                </button>
                <div className="carousel-content-wrapper">
                    <div className="carousel-content"
                         style={{ transform: `translateX(-${currentIndex * 100}%)` }}
                    >
                        <section className="slide slide_1">
                            <h2 className="slide_header">Chorwacja 08-09.2022</h2>
                            <p className="slide_text">Dwutygodniowy wyjazd do Chorwacji, gdzie dodatkowo zwiedzilismy takie kraje jak Albania i Czarnogora</p>
                        </section>
                        <section className="slide slide_1">
                            <h2 className="slide_header">Chorwacja 08-09.2022</h2>
                            <p className="slide_text">Dwutygodniowy wyjazd do Chorwacji, gdzie dodatkowo zwiedzilismy takie kraje jak Albania i Czarnogora</p>
                        </section>
                        <section className="slide slide_1">
                            <h2 className="slide_header">Chorwacja 08-09.2022</h2>
                            <p className="slide_text">Dwutygodniowy wyjazd do Chorwacji, gdzie dodatkowo zwiedzilismy takie kraje jak Albania i Czarnogora</p>
                        </section>
                    </div>
                </div>
                <button onClick={next} className="right-arrow">
                    &gt;
                </button>
            </div>
        </div>
    );
};

export default Carousel;

