import React from 'react';
import "./_films.scss"



const Films = ({link, title, image, text}) => {
    return (
        <>
            <div className="films_box">
                <a href={link} className="films_box_link">
                    <img src={image} alt="image" className="films_image"/>
                    <h2 className="films_title">{title}</h2>
                    <p className="films_text">{text}</p>
                </a>
            </div>
        </>
    );
};

export default Films;
