import React, { useState } from 'react';
import Conditions from './Conditions';

const Forecast = () => {
    let [responseObj, setResponseObj] = useState("Dubrownik");
    const API_key = "dad69a5e01ddbe2e693acdd610cf1fd8";
    const city_name = responseObj;
    function getForecast() {
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city_name}&appid=${API_key}`)
            .then(response => response.json())
            .then(response => {setResponseObj(response)})
        // weather data fetch function will go here
    }
    return (
        <div>
            <h2>Find Current Weather Conditions</h2>
            <button onClick={getForecast}>Get Forecast</button>
            <div>
                <p><strong>{responseObj.name}</strong></p>
                <p>It is currently {Math.round(responseObj.main.temp)} degrees out with {responseObj.weather[0].description}.</p>
            </div>
            />
        </div>
    )
}
export default Forecast;
